SELECT forms.form_nam, forms.form_contact, forms.form_tlf, forms.form_id, forms.form_inc, forms.form_url, forms.form_toolkit, CASE WHEN forms.form_active = 1 THEN 'Enabled' ELSE 'Disabled' END form_active FROM forms JOIN warehouses ON forms.wh_id = warehouses.wh_id WHERE forms.wh_id='{}' and warehouses.wh_active = 1